mapper.insert:
INSERT INTO SONG_MEMBER(MEMBER_NAME
				 , MEMBER_ID
				 , MEMBER_PASSWORD
				 , MEMBER_GENDER
				 , MEMBER_EMAIL1
				 , MEMBER_EMAIL2
				 , MEMBER_TEL1
				 , MEMBER_TEL2
				 , MEMBER_TEL3
				 , MEMBER_ZIPCODE
				 , MEMBER_ADDRESS1
				 , MEMBER_ADDRESS2
				 , MEMBER_REGISTER_DATE)
			VALUES(#{member_name}
			     , #{member_id}
			     , #{member_pwd}
			     , #{member_gender}
			     , #{member_email1}
			     , #{member_email2}
			     , #{member_tel1}
			     , #{member_tel2}
			     , #{member_tel3}
			     , #{member_zipcode}
			     , #{member_addr1}
			     , #{member_addr2}
			     , now())

mapper.update:
update member set name = #{member_name}
			     , #{member_id}
			     , #{member_pwd}
			     , #{member_gender}
			     , #{member_email1}
			     , #{member_email2}
			     , #{member_tel1}
			     , #{member_tel2}
			     , #{member_tel3}
			     , #{member_zipcode}
			     , #{member_addr1}
			     , #{member_addr2}

	            , logtime = now() where id = #{member_id}
